import React, { Component } from 'react';


export default class RaceChart extends Component{
 
    constructor(props){
      super(props);
      this.state = {
        data: [],
        year: "",
        min: Math.min(Object.keys(this.props.data))
      };
      console.log(Object.keys(this.props.data)[0])
    }
   
    // getRandomInt(min, max){
