    //     { id: 6, title: 'Ameen', value: this.getRandomInt(10, 90), color: '#423bcf' }
    //   ];
    //   this.setState({ data });
    //}
   
    componentDidMount()
    {
        let i=0
        for(let [key, value] of Object.entries(this.props.data))
        {
            this.setState({ data: value,
                            year: key});
            this.timer(i);
        }
    }

    timer()
    {
        setTimeout(function() {
            console.log("Timer")
        }, 2000);
    }

    render(){
      return(
        <div>
            <div className="row">
                <div className="col fixed-top">
                <div className="float-end m-5 p-5">
                    <h2>Year : {this.state.year}</h2>
                </div>
                </div>
                <ChartRace
                    data={this.state.data}
                    backgroundColor='white'
                    width={760}
                    padding={12}
                    itemHeight={58}
                    gap={12}
                    titleStyle={{ font: 'normal 400 13px Arial', color: '#000' }}
                    valueStyle={{ font: 'normal 400 11px Arial', color: '#000' }}
                />
          </div>
        </div>
      );
    }
   
  }
