import ChartRace from 'react-chart-race';
import React, { Component } from 'react';


export default class RaceChart extends Component{
 
    constructor(props){
      super(props);
      var years=Object.keys(this.props.data)
      this.state = {
        data: [],
        year: "",
        min: parseInt(years[0]),
        max: parseInt(years[years.length-1])
      };
      console.log(this.state.min,this.state.max)
    }
   
    // getRandomInt(min, max){
    //   min = Math.ceil(min);
    //   max = Math.floor(max);
    //   return Math.floor(Math.random() * (max - min + 1)) + min;
    // }
   
    // handleChange(){
    //   const data = [
    //     { id: 0, title: 'Ayfonkarahisar', value: this.getRandomInt(10, 90), color: "rgb(123,22,200)" },
    //     { id: 1, title: 'Kayseri', value: 38, color: '#3fc42d' },
    //     { id: 2, title: 'Muğla', value: this.getRandomInt(10, 90), color: '#c33178' },
    //     { id: 3, title: 'Uşak', value: this.getRandomInt(10, 90), color: '#423bce' },
    //     { id: 4, title: 'Sivas', value: 58, color: '#c8303b' },
    //     { id: 5, title: 'Konya', value: 16, color: '#2c2c2c' },
    //     { id: 6, title: 'Ameen', value: this.getRandomInt(10, 90), color: '#423bcf' }
    //   ];
    //   this.setState({ data });
    //}
   
    componentDidMount()
    {
        let i=this.state.min
        setInterval(() => {
            if(i<=this.state.max)
            {
                this.setState({ data: this.props.data[i],
                    year: i});
                    i=i+1
            }
          }, 2000);
    }


    render(){
      return(
        <div>
            <div className="row">
                <div className="col fixed-top">
                <div className="float-end m-5 p-5">
                    <h2>Year : {this.state.year}</h2>
                </div>
                </div>
                <ChartRace
                    data={this.state.data}
                    backgroundColor='white'
                    width={760}
                    padding={12}
                    itemHeight={58}
                    gap={12}
                    titleStyle={{ font: 'normal 400 13px Arial', color: '#000' }}
                    valueStyle={{ font: 'normal 400 11px Arial', color: '#000' }}
                />
          </div>
        </div>
      );
    }
   
  }
